// Verkaufszahlen aus Input extrahieren
const verkaufsZahlen = [
  $input.first().json.verkauf_1,
  $input.first().json.verkauf_2,
  $input.first().json.verkauf_3,
  $input.first().json.verkauf_4,
  $input.first().json.verkauf_5
];

// Berechnungen durchführen
const summe = verkaufsZahlen.reduce((acc, val) => acc + val, 0);
const durchschnitt = summe / verkaufsZahlen.length;
const maximum = Math.max(...verkaufsZahlen);
const minimum = Math.min(...verkaufsZahlen);

// Sortierte Liste erstellen
const sortiert = [...verkaufsZahlen].sort((a, b) => b - a);

// Ergebnis zurückgeben
return [{
  json: {
    original_daten: verkaufsZahlen,
    gesamtsumme: summe,
    durchschnitt: Math.round(durchschnitt * 100) / 100,
    hoechster_verkauf: maximum,
    niedrigster_verkauf: minimum,
    sortiert_absteigend: sortiert,
    anzahl_verkauefe: verkaufsZahlen.length,
    datum: new Date().toISOString().split('T')[0]
  }
}];
