// Onboarding-Tasks f√ºr neuen Mitarbeiter generieren
const mitarbeiter = $input.first().json;

const tasks = [
  {
    titel: 'E-Mail-Account einrichten',
    verantwortlich: 'IT',
    prioritaet: 'hoch',
    frist_tage: 3
  },
  {
    titel: 'Hardware bereitstellen (Laptop, Monitor)',
    verantwortlich: 'IT',
    prioritaet: 'hoch',
    frist_tage: 5
  },
  {
    titel: 'Arbeitsplatz vorbereiten',
    verantwortlich: 'Facility',
    prioritaet: 'mittel',
    frist_tage: 7
  },
  {
    titel: 'Zug√§nge zu Systemen einrichten',
    verantwortlich: 'IT',
    prioritaet: 'hoch',
    frist_tage: 3
  },
  {
    titel: 'Onboarding-Buddy zuweisen',
    verantwortlich: 'HR',
    prioritaet: 'mittel',
    frist_tage: 5
  },
  {
    titel: 'Welcome-Package vorbereiten',
    verantwortlich: 'HR',
    prioritaet: 'niedrig',
    frist_tage: 7
  }
];

// F√ºr jede Task ein Item erstellen
const taskItems = tasks.map(task => {
  const fristDatum = new Date();
  fristDatum.setDate(fristDatum.getDate() + task.frist_tage);

  return {
    json: {
      mitarbeiter_id: mitarbeiter.id,
      mitarbeiter_name: `${mitarbeiter.vorname} ${mitarbeiter.nachname}`,
      mitarbeiter_email: mitarbeiter.email,
      task_titel: task.titel,
      verantwortlich: task.verantwortlich,
      prioritaet: task.prioritaet,
      frist: fristDatum.toISOString().split('T')[0],
      status: 'offen'
    }
  };
});

return taskItems;

// Alle Tasks f√ºr Zusammenfassung sammeln
const tasks = $input.all();
const mitarbeiter = tasks[0].json;

const tasksByDepartment = {};

tasks.forEach(task => {
  const dept = task.json.verantwortlich;
  if (!tasksByDepartment[dept]) {
    tasksByDepartment[dept] = [];
  }
  tasksByDepartment[dept].push({
    titel: task.json.task_titel,
    prioritaet: task.json.prioritaet,
    frist: task.json.frist
  });
});

return [{
  json: {
    mitarbeiter_name: mitarbeiter.mitarbeiter_name,
    mitarbeiter_email: mitarbeiter.mitarbeiter_email,
    tasks_nach_abteilung: tasksByDepartment,
    gesamt_tasks: tasks.length
  }
}];

