// Benachrichtigungen f√ºr einzelne Abteilungen vorbereiten
const data = $input.first().json;
const departments = ['IT', 'HR', 'Facility'];

const notifications = departments.map(dept => {
  const deptTasks = data.tasks_nach_abteilung[dept] || [];

  return {
    json: {
      abteilung: dept,
      mitarbeiter_name: data.mitarbeiter_name,
      tasks: deptTasks,
      anzahl_tasks: deptTasks.length
    }
  };
}).filter(item => item.json.anzahl_tasks > 0);

return notifications;
